import React, {Component} from "react";

class AddBook extends Component {

    constructor(){
        super();

        this.state = {
            "title":"",
            "number":""
        }

        this.onChange = this.onChange.bind(this);
    }

    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }

    render() {
        return (
            <div>
                <form>
                    <div style={{padding:"90px"}}>
                        <div className="form-group">
                            <label htmlFor="inputTitle">Book Title</label>
                            <input type="title" className="form-control" id="inputTitle"
                                   placeholder="Enter Book Title"
                                   name="title"
                                   value={this.state.title}
                                   onChange = {this.onChange}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputNumber">Book Number</label>
                            <input type="number" name="number" value={this.state.number} className="form-control" id="inputNumber" placeholder="Enter Book Number"/>
                        </div>
                        <input type="submit" className="btn btn-primary mt-4" />
                    </div>
                </form>
            </div>
        )
    }
}

export default AddBook;
