import {ADD_BOOK} from "./types";
import axios from 'axios';

const apiURL = "http://localhost:3000/api/book/"

export const createBook = ({title, number}) => {
    return (dispatch) => {
        return axios.post(`${apiURL}/new`, {title, number})
            .then(response => {
                dispatch(createBookSuccess(response.data))
            })
            .catch(error => {
                throw(error);
            });
    };
};

export const createBookSuccess = (book) => {
    return {
        type: ADD_BOOK,
        payload: {
            id: book.id,
            title: book.title,
            number: book.number
        }
    }
};
